/**
 * Default ICC profile for Ultra HDR JPEG output.
 * Same profile as in the reference memorial.jpg so Apple Preview recognizes the file as 10-bit.
 * Format: JPEG APP2 payload (ICC_PROFILE\0 + chunk index + total chunks + profile bytes).
 */
const ICC_BASE64 =
  // biome-ignore lint/security/noSecrets: ICC profile from reference memorial.jpg (public asset), not a credential
  '';

function decodeBase64ToBytes(b64: string): Uint8Array {
  if (typeof Buffer !== 'undefined') {
    return new Uint8Array(Buffer.from(b64, 'base64'));
  }
  const binary = atob(b64);
  const bytes = new Uint8Array(binary.length);
  for (let i = 0; i < binary.length; i++) {
    bytes[i] = binary.charCodeAt(i);
  }
  return bytes;
}

/** Default ICC profile (APP2 payload) to embed in Ultra HDR JPEGs for Apple Preview 10-bit recognition. */
export const DEFAULT_ICC_PROFILE: Uint8Array = decodeBase64ToBytes(ICC_BASE64);
